import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import PatientList from './components/PatientList';
import AddPatient from './components/AddPatient';
import EditPatient from './components/EditPatient';
import MedicalHistory from './components/MedicalHistory';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Navbar() {
  const navigate = useNavigate();
  const [search, setSearch] = React.useState('');

  const handleSearch = () => {
    navigate(`/?search=${encodeURIComponent(search)}`);
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light mb-4">
      <div className="container-fluid">
        <Link className="navbar-brand fw-bold" to="/">ðŸ¦· Clinic</Link>
        <div className="d-flex">
          <input
            type="text"
            className="form-control me-2"
            placeholder="Search by name"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <button className="btn btn-outline-primary me-2" onClick={handleSearch}>Search</button>
          <Link to="/" className="btn btn-outline-secondary me-2">Home</Link>
          <Link to="/add" className="btn btn-success">Add Patient</Link>
        </div>
      </div>
    </nav>
  );
}

function App() {
  return (
    <Router>
      <div className="container mt-4">
		<Navbar />
        <h2 className="text-center mb-4">Vathare Dental Clinic</h2>
        <Routes>
          <Route path="/" element={<PatientList />} />
          <Route path="/add" element={<AddPatient />} />
          <Route path="/edit/:id" element={<EditPatient />} />
          <Route path="/history/:id" element={<MedicalHistory />} />
        </Routes>
        <ToastContainer position="top-right" autoClose={2000} />
      </div>
    </Router>
  );
}

export default App;
