import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from "react-toastify";

function PatientList() {
  const [patients, setPatients] = useState([]);
  const [keyword, setKeyword] = useState('');

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    const result = await axios.get('http://localhost:8080/api/patients');
    setPatients(result.data);
  };

  const searchPatients = async () => {
    const result = await axios.get(`http://localhost:8080/api/patients/search?keyword=${keyword}`);
    setPatients(result.data);
  };

  const deleteHistory = async (hid) => {
		try {
			console.log("deleting record");

			await axios.delete(`http://localhost:8080/api/history/${hid}`);
			toast.success('History deleted successfully!');
			fetchPatients();
		} catch (error) {
			console.error(error);
			toast.error('Failed to delete history.');
		}
	};

	const confirmAndDelete = (hid) => {
		console.log("Inside confirmAndDelete()");

		if (window.confirm('Are you sure you want to delete this history record?')) {
			deleteHistory(hid);
		}
	};

  // const deletePatient = async (id) => {
  //   await axios.delete(`http://localhost:8080/api/patients/${id}`);
  //   fetchPatients();
  // };

  return (
    <div>
      <div className="d-flex justify-content-between mb-3">
        <input
          type="text"
          className="form-control me-2"
          style={{ maxWidth: '300px' }}
          placeholder="Search patients..."
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <button className="btn btn-outline-primary me-2" onClick={searchPatients}>
          Search
        </button>
        <Link to="/add" className="btn btn-primary">
          Add Patient
        </Link>
      </div>
      <table className="table table-bordered table-hover">
        <thead className="table-light">
          <tr>
            <th>Name</th>
            <th>Age</th>
            <th>Contact</th>
            <th>Address</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient) => (
            <tr key={patient.id}>
              <td>{patient.name}</td>
              <td>{patient.age}</td>
              <td>{patient.contact}</td>
              <td>{patient.address}</td>
              <td>
                <Link to={`/edit/${patient.id}`} className="btn btn-sm btn-warning me-2">
                  Edit
                </Link>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => confirmAndDelete(patient.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PatientList;
