import { React, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import PatientList from './components/PatientList';
import AddPatient from './components/AddPatient';
import EditPatient from './components/EditPatient';
import MedicalHistory from './components/MedicalHistory';
import 'bootstrap/dist/css/bootstrap.min.css';
import { toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

function Navbar() {
	const [keyword, setKeyword] = useState('');
	const [patients, setPatients] = useState([]);

	const searchPatients = async () => {
		const result = await axios.get(`http://localhost:8080/api/patients/search?name=${keyword}`);
		setPatients(result.data);
	};

	return (
		<nav className="navbar navbar-expand-lg navbar-light bg-light mb-4">
			<div className="container-fluid">
				<Link className="navbar-brand fw-bold" to="/">Vathare ðŸ¦· Clinic</Link>
				<div className="d-flex">
					<input
						type="text"
						className="form-control me-2"
						style={{ maxWidth: '300px' }}
						placeholder="Search patients..."
						value={keyword}
						onChange={(e) => setKeyword(e.target.value)}
					/>
					<button className="btn btn-outline-primary me-2" onClick={searchPatients}>
						Search
					</button>
					<Link to="/" className="btn btn-outline-secondary me-2">Home</Link>
					<Link to="/add" className="btn btn-success me-2">Add Patient</Link>
				</div>
			</div>
		</nav>
	);
}

function App() {
	const [patients, setPatients] = useState([]);
	const fetchPatients = async () => {
		const result = await axios.get('http://localhost:8080/api/patients');
		setPatients(result.data);
	};
	const deleteHistory = async (hid) => {
		try {
			console.log("deleting record");

			await axios.delete(`http://localhost:8080/api/patients/${hid}`);
			toast.success('History deleted successfully!');
			fetchPatients();
		} catch (error) {
			console.error(error);
			toast.error('Failed to delete history.');
		}
	};
	const confirmAndDelete = (hid) => {
			toast.info(
				<div>
					<p className="mb-2">Are you sure you want to delete this history record?</p>
					<div className="d-flex justify-content-end gap-2">
						<button className="btn btn-sm btn-danger" onClick={() => {
							deleteHistory(hid);
							toast.dismiss(); // Close the toast after confirmation
						}}>
							Yes
						</button>
						<button className="btn btn-sm btn-secondary" onClick={() => toast.dismiss()}>
							No
						</button>
					</div>
				</div>,
				{
					autoClose: false,
					closeOnClick: false,
					closeButton: false,
					draggable: false,
				}
			);
		};
	return (
		<Router>
			<div className="container mt-4">
				<Navbar />
				{/* <h2 className="text-center mb-4">Vathare Dental Clinic</h2> */}
				<table className="table table-bordered table-hover">
					<thead className="table-light">
						<tr>
							<th>Name</th>
							<th>Gender</th>
							<th>Age</th>
							<th>Contact</th>
							<th>Address</th>
							<th>Actions</th>
						</tr>
					</thead>
					<tbody>
						{patients.map((patient) => (
							<tr key={patient.id}>
								<td>{patient.name}</td>
								<td>{patient.gender}</td>
								<td>{patient.age}</td>
								<td>{patient.contact}</td>
								<td>{patient.address}</td>
								<td>
									<Link to={`/edit/${patient.id}`} className="btn btn-outline-warning me-2">
										Edit
									</Link>
									<Link to={`/history/${patient.id}`} className="btn btn-outline-primary me-2">
										History
									</Link>
									<button
										className="btn btn-sm btn-danger me-2"
										onClick={() => confirmAndDelete(patient.id)}
									>
										Delete
									</button>
								</td>
							</tr>
						))}
					</tbody>
				</table>
				<Routes>
					<Route path="/" element={<PatientList />} />
					<Route path="/add" element={<AddPatient />} />
					<Route path="/edit/:id" element={<EditPatient />} />
					<Route path="/history/:id" element={<MedicalHistory />} />
				</Routes>
				<ToastContainer position="top-right" autoClose={2000} />
			</div>
		</Router>
	);
}

export default App;
